// Complete the palindromeIndex function below.
function palindromeIndex($s) {
    $s = str_split($s);

    // $isPossible = isPalindromeString($s);
    // if (!$isPossible) return -1;

    $size = sizeof($s);
    
    for ($i=0, $j=$size-1; $i < $size/2; $i++,$j--) { 

        if ($s[$i] != $s[$j]) {
            if ($s[$i+1] == $s[$i]) {
                return $j;
            }
            return $i;
        }
    }

    return -1;
}

